mapper.select:
SELECT * FROM user_rank_record
WHERE user_seq IN (SELECT user_seq FROM User WHERE user_id = #{userId})
ORDER BY ${con}

SELECT * FROM user_rank_record
WHERE user_seq IN (SELECT user_seq FROM Group_User WHERE group_id = #{groupId})
ORDER BY ${con}

SELECT * FROM `Group`
WHERE group_admin IN (SELECT user_seq FROM User WHERE user_id = #{userId})
ORDER BY ${con}
